import { Component } from '@angular/core';
import Chart from 'chart.js/auto';
import { SharedServiceService } from '../shared-service.service';

@Component({
  selector: 'app-graph',
  templateUrl: './graph.component.html',
  styleUrls: ['./graph.component.css']
})
export class GraphComponent {

  public chart: any
  ngOnInit(): void {
    this.createChart();
  }
  constructor(private db:SharedServiceService){}
  async createChart() {
    this.db.getPositive().subscribe(data=>{
      console.log(data)
      const dates = Object.keys(data).map(key => new Date(key).toLocaleDateString(undefined, { day: 'numeric', month: 'short' }));
    const counts = Object.values(data);
    console.log(data)

    // Create the chart

    this.chart = new Chart("MyChart", {
      type: 'line',
      data: {
        labels: dates,
        datasets: [{
          label: 'Number of positive cases',
          data: counts,
          backgroundColor: 'lightblue'
        }]
      },
     options: {
        aspectRatio:2.5
      }
    })


    });
  }

  ngOnDestroy(): void {
    if (this.chart) {
      this.chart.destroy();
    }
  }
