<button type="button" class="btn btn-info float-left m-2" data-toggle="modal" data-target="#exampleModal"
    data-backdrop="static" data-keyboard="false">
    הוספת חיסון
</button>

<!-- Modal -->
<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel"
    aria-hidden="true">
    <div class="modal-dialog modal-dialog-centered modal-xl" role="document">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title" id="exampleModalLabel">הוספת פציינט</h5>
                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div class="modal-body">
                <app-add-vaccinations></app-add-vaccinations>
            </div>
        </div>
    </div>
</div>
<table class="table justify-content-center">
    <thead>
        <tr>
            <th>
                <div class="d-flex flex-row">
                    <!-- <input [(ngModel)]="NameFilter" class="form-control" (keyup)="Filter()" placeholder="Filter"> -->
                </div>
                שם היצרן
            </th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let item of arrv">
            <td>{{item.manufacturer}}</td>
        </tr>
    </tbody>
</table>
