import { Component } from '@angular/core';
import { SharedServiceService } from 'src/app/shared-service.service';
import { Vaccine } from 'src/app/Vaccine';
import { Patient } from '../Patient';

@Component({
  selector: 'app-show-patients',
  templateUrl: './show-patients.component.html',
  styleUrls: ['./show-patients.component.css']
})
export class ShowPatientsComponent {
  arrp:Patient[]=[];
   arrV:any[]=[]
  ActivateComp:boolean=false;
  constructor(private db:SharedServiceService){}
  ngOnInit(): void {
    this.RefreshPatientsLst();
    console.log(this.ActivateComp);

  }
  RefreshPatientsLst(){
    this.db.GetAllUsers() .subscribe(x=>{     
      this.arrp = x.map(patient => {
        // Extract the date of birth value from the patient object
        const dateOfBirth = new Date(patient.dateOfBirth);

        // Modify the date of birth value as needed
        const modifiedDateOfBirth = new Date(dateOfBirth.setFullYear(dateOfBirth.getFullYear() - 1));

        // Return a new patient object with the modified date of birth value
        return { ...patient, dateOfBirth: modifiedDateOfBirth.toISOString() };
      });

    });
  }
  addClick(){
    this.ActivateComp=true;
  }
  closeClick(){
    this.ActivateComp=false;
    this.RefreshPatientsLst();
  }
  show(item:Patient)
  {
    console.log(item.id);

    this.db.getVaccinesForPatient(item.id).subscribe(x=>{


      this.arrV=<any>x;
      console.log(this.arrV);
    })
  }
}
